import React from 'react';
import {Link} from 'react-router-dom';
import '../style/form.css'
import Axios from 'axios'

export default class Form extends React.Component{
    enter = () => {
        console.log("enter")
        const player = document.getElementById("name").value; 
        this.props.addPlayer(player)
        console.log(this.props.players)
    }
    createPlayer = () => {
        let body = {
            name: document.getElementById("name").value
        }
        Axios.post(
            'http://localhost:3000/user',
            JSON.stringify(body)
        )
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        })
    }
    host() {
        console.log("host")
    }
    render() {
        return (<div>
            <div className = "form">
            <input placeholder = "name" id = "name"></input>
            <button onClick = {this.enter}>Confirm Name</button>
            <button onClick = {this.host}>Host</button>
            </div>
            <div className = "name">
            <p>{this.props.players}</p>
            </div>
            <div className = "link">
            <Link onClick = {this.createPLayer}className = "linkButton" to="/enter">Enter</Link>

            </div>
        </div>)
      }
}
