import React from 'react';
import {Link} from 'react-router-dom';
import '../style/form.css'
import Axios from 'axios'

export default class Form extends React.Component{
    enter = () => {
        console.log("enter")
        const player = document.getElementById("name").value; 
        this.props.addPlayer(player)
        console.log(this.props.players)
    }
    createPlayer = () => {
        console.log('create player')
        const name = this.getInputValue("name")
        const room = this.getInputValue("room")

        //const room = document.getElementById("room").value;
        let body = {
            name: name,
            room: room
        }
        Axios.post(
            '/users',
            (body)
        )
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        })
    }
    getInputValue = (inputName) =>{
        var inputVal = document.getElementsByClassName(inputName).value;
        return inputVal
    }
    host() {
        console.log("host")
    }
    render() {
        return (<div>
            <div className = "form">
            <input type = "text" className = "name" placeholder = "name" id = "name"></input>
            <input className = "room" placeholder = "room" id = "room"></input>
            <button onClick = {this.createPlayer()}>Confirm Name</button>
            <button onClick = {this.host}>Host</button>
            </div>
            <div className = "nameSpot">
            <p>{this.props.players}</p>
            </div>
            <div className = "link">
            <Link onClick = {this.createPlayer()} className = "linkButton" to="/enter">Enter</Link>

            </div>
        </div>)
      }
}
