import React from 'react';
import {Link} from 'react-router-dom';
import '../style/form.css'
import Axios from 'axios'

export default class Form extends React.Component{
    enter = () => {
        console.log("enter")
        const player = document.getElementById("name").value; 
        this.props.addPlayer(player)
        console.log(this.props.players)
    }
    createPlayer = () => {
        console.log('createplayer')
        name = document.getElementById("name").value; 
        room = document.getElementById("room").value;
        console.log(room)
        let body = {
            name: document.getElementById("name").value,
            room: document.getElementById("room").value
        }
        Axios.post(
            'localhost:3050/users',
            JSON.stringify(body)
        )
        .then((response) => {
            console.log(response)
        })
        .catch((error) => {
            console.log(error)
        })
    }
    host() {
        console.log("host")
    }
    render() {
        return (<div>
            <div className = "form">
            <input className = "name" placeholder = "name" id = "name"></input>
            <input className = "room" placeholder = "room" id = "room"></input>
            <button onClick = {this.createPlayer()}>Confirm Name</button>
            <button onClick = {this.host}>Host</button>
            </div>
            <div className = "nameSpot">
            <p>{this.props.players}</p>
            </div>
            <div className = "link">
            <Link onClick = {this.createPLayer()} className = "linkButton" to="/enter">Enter</Link>

            </div>
        </div>)
      }
}
